    // DECODE RESULT SET 
    ///////////////////////////////////////////////////////////////////////
    /**
     * Transforms a ResultSet iterating on the agent on a AgentBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting AgentBean pObject
     */
    //72
    public AgentBean decodeRow(ResultSet rs) throws SQLException
    {
        AgentBean pObject = createAgentBean();
        pObject.setAgentid(Manager.getLong(rs, 1));
        pObject.setType(Manager.getBoolean(rs, 2));
        pObject.setLname(rs.getString(3));
        pObject.setFname(rs.getString(4));
        pObject.setName(rs.getString(5));
        pObject.setIdnum(rs.getString(6));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the agent table on a AgentBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting AgentBean pObject
     */
    //73
    public AgentBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        AgentBean pObject = createAgentBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_AGENTID:
                    ++pos;
                    pObject.setAgentid(Manager.getLong(rs, pos));
                    break;
                case ID_TYPE:
                    ++pos;
                    pObject.setType(Manager.getBoolean(rs, pos));
                    break;
                case ID_LNAME:
                    ++pos;
                    pObject.setLname(rs.getString(pos));
                    break;
                case ID_FNAME:
                    ++pos;
                    pObject.setFname(rs.getString(pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_IDNUM:
                    ++pos;
                    pObject.setIdnum(rs.getString(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

