    /**
     * Transforms a ResultSet iterating on the amenity on a AmenityBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting AmenityBean pObject
     */
    //72
    public AmenityBean decodeRow(ResultSet rs) throws SQLException
    {
        AmenityBean pObject = createAmenityBean();
        pObject.setAmenityid(Manager.getInteger(rs, 1));
        pObject.setName(rs.getString(2));
        pObject.setType(Manager.getInteger(rs, 3));
        pObject.setOrd(Manager.getInteger(rs, 4));
        pObject.setRegbyid(Manager.getInteger(rs, 5));
        pObject.setRegdate(rs.getTimestamp(6));
        pObject.setActive(Manager.getBoolean(rs, 7));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the amenity table on a AmenityBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting AmenityBean pObject
     */
    //73
    public AmenityBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        AmenityBean pObject = createAmenityBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_AMENITYID:
                    ++pos;
                    pObject.setAmenityid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_TYPE:
                    ++pos;
                    pObject.setType(Manager.getInteger(rs, pos));
                    break;
                case ID_ORD:
                    ++pos;
                    pObject.setOrd(Manager.getInteger(rs, pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_ACTIVE:
                    ++pos;
                    pObject.setActive(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

