    /**
     * Transforms a ResultSet iterating on the bedtype on a BedtypeBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting BedtypeBean pObject
     */
    //72
    public BedtypeBean decodeRow(ResultSet rs) throws SQLException
    {
        BedtypeBean pObject = createBedtypeBean();
        pObject.setBedtypeid(Manager.getInteger(rs, 1));
        pObject.setCode(rs.getString(2));
        pObject.setName(rs.getString(3));
        pObject.setRegbyid(Manager.getInteger(rs, 4));
        pObject.setRegdate(rs.getTimestamp(5));
        pObject.setActive(Manager.getBoolean(rs, 6));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the bedtype table on a BedtypeBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting BedtypeBean pObject
     */
    //73
    public BedtypeBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        BedtypeBean pObject = createBedtypeBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_BEDTYPEID:
                    ++pos;
                    pObject.setBedtypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_CODE:
                    ++pos;
                    pObject.setCode(rs.getString(pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_ACTIVE:
                    ++pos;
                    pObject.setActive(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

