    /**
     * Transforms a ResultSet iterating on the blockroom on a BlockroomBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting BlockroomBean pObject
     */
    //72
    public BlockroomBean decodeRow(ResultSet rs) throws SQLException
    {
        BlockroomBean pObject = createBlockroomBean();
        pObject.setBlockroomid(Manager.getLong(rs, 1));
        pObject.setRoomid(Manager.getInteger(rs, 2));
        pObject.setBlockstart(rs.getDate(3));
        pObject.setBlockend(rs.getDate(4));
        pObject.setReasonid(Manager.getInteger(rs, 5));
        pObject.setNote(rs.getString(6));
        pObject.setRegdate(rs.getTimestamp(7));
        pObject.setRegbyid(Manager.getInteger(rs, 8));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the blockroom table on a BlockroomBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting BlockroomBean pObject
     */
    //73
    public BlockroomBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        BlockroomBean pObject = createBlockroomBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_BLOCKROOMID:
                    ++pos;
                    pObject.setBlockroomid(Manager.getLong(rs, pos));
                    break;
                case ID_ROOMID:
                    ++pos;
                    pObject.setRoomid(Manager.getInteger(rs, pos));
                    break;
                case ID_BLOCKSTART:
                    ++pos;
                    pObject.setBlockstart(rs.getDate(pos));
                    break;
                case ID_BLOCKEND:
                    ++pos;
                    pObject.setBlockend(rs.getDate(pos));
                    break;
                case ID_REASONID:
                    ++pos;
                    pObject.setReasonid(Manager.getInteger(rs, pos));
                    break;
                case ID_NOTE:
                    ++pos;
                    pObject.setNote(rs.getString(pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

