    /**
     * Transforms a ResultSet iterating on the bsource on a BsourceBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting BsourceBean pObject
     */
    //72
    public BsourceBean decodeRow(ResultSet rs) throws SQLException
    {
        BsourceBean pObject = createBsourceBean();
        pObject.setBsourceid(Manager.getInteger(rs, 1));
        pObject.setCode(rs.getString(2));
        pObject.setName(rs.getString(3));
        pObject.setRegbyid(Manager.getInteger(rs, 4));
        pObject.setRegdate(rs.getTimestamp(5));
        pObject.setActive(Manager.getBoolean(rs, 6));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the bsource table on a BsourceBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting BsourceBean pObject
     */
    //73
    public BsourceBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        BsourceBean pObject = createBsourceBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_BSOURCEID:
                    ++pos;
                    pObject.setBsourceid(Manager.getInteger(rs, pos));
                    break;
                case ID_CODE:
                    ++pos;
                    pObject.setCode(rs.getString(pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_ACTIVE:
                    ++pos;
                    pObject.setActive(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

