    /**
     * Transforms a ResultSet iterating on the housekeepingstatus on a HousekeepingstatusBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting HousekeepingstatusBean pObject
     */
    //72
    public HousekeepingstatusBean decodeRow(ResultSet rs) throws SQLException
    {
        HousekeepingstatusBean pObject = createHousekeepingstatusBean();
        pObject.setHousekeepingstatusid(Manager.getInteger(rs, 1));
        pObject.setName(rs.getString(2));
        pObject.setColor(rs.getString(3));
        pObject.setRegbyid(Manager.getInteger(rs, 4));
        pObject.setRegdate(rs.getTimestamp(5));
        pObject.setActive(Manager.getBoolean(rs, 6));
        pObject.setDeleted(Manager.getBoolean(rs, 7));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the housekeepingstatus table on a HousekeepingstatusBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting HousekeepingstatusBean pObject
     */
    //73
    public HousekeepingstatusBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        HousekeepingstatusBean pObject = createHousekeepingstatusBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_HOUSEKEEPINGSTATUSID:
                    ++pos;
                    pObject.setHousekeepingstatusid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_COLOR:
                    ++pos;
                    pObject.setColor(rs.getString(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_ACTIVE:
                    ++pos;
                    pObject.setActive(Manager.getBoolean(rs, pos));
                    break;
                case ID_DELETED:
                    ++pos;
                    pObject.setDeleted(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

