    /**
     * Transforms a ResultSet iterating on the houseunit on a HouseunitBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting HouseunitBean pObject
     */
    //72
    public HouseunitBean decodeRow(ResultSet rs) throws SQLException
    {
        HouseunitBean pObject = createHouseunitBean();
        pObject.setHouseunitid(Manager.getInteger(rs, 1));
        pObject.setName(rs.getString(2));
        pObject.setRegbyid(Manager.getInteger(rs, 3));
        pObject.setRegdate(rs.getTimestamp(4));
        pObject.setActive(Manager.getBoolean(rs, 5));
        pObject.setDeteted(Manager.getBoolean(rs, 6));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the houseunit table on a HouseunitBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting HouseunitBean pObject
     */
    //73
    public HouseunitBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        HouseunitBean pObject = createHouseunitBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_HOUSEUNITID:
                    ++pos;
                    pObject.setHouseunitid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_ACTIVE:
                    ++pos;
                    pObject.setActive(Manager.getBoolean(rs, pos));
                    break;
                case ID_DETETED:
                    ++pos;
                    pObject.setDeteted(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

