    // DECODE RESULT SET 
    ///////////////////////////////////////////////////////////////////////
    /**
     * Transforms a ResultSet iterating on the idtype on a IdtypeBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting IdtypeBean pObject
     */
    //72
    public IdtypeBean decodeRow(ResultSet rs) throws SQLException
    {
        IdtypeBean pObject = createIdtypeBean();
        pObject.setIdtypeid(Manager.getInteger(rs, 1));
        pObject.setName(rs.getString(2));
        pObject.setActive(Manager.getBoolean(rs, 3));
        pObject.setDeleted(Manager.getBoolean(rs, 4));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the idtype table on a IdtypeBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting IdtypeBean pObject
     */
    //73
    public IdtypeBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        IdtypeBean pObject = createIdtypeBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_IDTYPEID:
                    ++pos;
                    pObject.setIdtypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_ACTIVE:
                    ++pos;
                    pObject.setActive(Manager.getBoolean(rs, pos));
                    break;
                case ID_DELETED:
                    ++pos;
                    pObject.setDeleted(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

