    /**
     * Transforms a ResultSet iterating on the ordermain on a OrdermainBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting OrdermainBean pObject
     */
    //72
    public OrdermainBean decodeRow(ResultSet rs) throws SQLException
    {
        OrdermainBean pObject = createOrdermainBean();
        pObject.setOrdermainid(Manager.getLong(rs, 1));
        pObject.setHallname(rs.getString(2));
        pObject.setTablename(rs.getString(3));
        pObject.setOpendate(rs.getTimestamp(4));
        pObject.setClosedate(rs.getTimestamp(5));
        pObject.setWaitername(rs.getString(6));
        pObject.setAmount(Manager.getDouble(rs, 7));
        pObject.setDiscount(Manager.getDouble(rs, 8));
        pObject.setPrice(Manager.getDouble(rs, 9));
        pObject.setCash(Manager.getDouble(rs, 10));
        pObject.setPaid(Manager.getDouble(rs, 11));
        pObject.setRegname(rs.getString(12));
        pObject.setRegdate(rs.getTimestamp(13));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the ordermain table on a OrdermainBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting OrdermainBean pObject
     */
    //73
    public OrdermainBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        OrdermainBean pObject = createOrdermainBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_ORDERMAINID:
                    ++pos;
                    pObject.setOrdermainid(Manager.getLong(rs, pos));
                    break;
                case ID_HALLNAME:
                    ++pos;
                    pObject.setHallname(rs.getString(pos));
                    break;
                case ID_TABLENAME:
                    ++pos;
                    pObject.setTablename(rs.getString(pos));
                    break;
                case ID_OPENDATE:
                    ++pos;
                    pObject.setOpendate(rs.getTimestamp(pos));
                    break;
                case ID_CLOSEDATE:
                    ++pos;
                    pObject.setClosedate(rs.getTimestamp(pos));
                    break;
                case ID_WAITERNAME:
                    ++pos;
                    pObject.setWaitername(rs.getString(pos));
                    break;
                case ID_AMOUNT:
                    ++pos;
                    pObject.setAmount(Manager.getDouble(rs, pos));
                    break;
                case ID_DISCOUNT:
                    ++pos;
                    pObject.setDiscount(Manager.getDouble(rs, pos));
                    break;
                case ID_PRICE:
                    ++pos;
                    pObject.setPrice(Manager.getDouble(rs, pos));
                    break;
                case ID_CASH:
                    ++pos;
                    pObject.setCash(Manager.getDouble(rs, pos));
                    break;
                case ID_PAID:
                    ++pos;
                    pObject.setPaid(Manager.getDouble(rs, pos));
                    break;
                case ID_REGNAME:
                    ++pos;
                    pObject.setRegname(rs.getString(pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

