    public PersonnelBean decodeRow(ResultSet rs) throws SQLException
    {
        PersonnelBean pObject = createPersonnelBean();
        pObject.setPersonnelid(Manager.getInteger(rs, 1));
        pObject.setFname(rs.getString(2));
        pObject.setLname(rs.getString(3));
        pObject.setPhone(rs.getString(4));
        pObject.setPersonneltypeid(Manager.getInteger(rs, 5));
        pObject.setLoginid(rs.getString(6));
        pObject.setPassword(rs.getString(7));
        pObject.setEmail(rs.getString(8));
        pObject.setAddress(rs.getString(9));
        pObject.setRegdate(rs.getTimestamp(10));
        pObject.setDeleted(Manager.getBoolean(rs, 11));
        pObject.setChangebyid(Manager.getInteger(rs, 12));
        pObject.setChangedate(rs.getTimestamp(13));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the personnel table on a PersonnelBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting PersonnelBean pObject
     */
    //73
    public PersonnelBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        PersonnelBean pObject = createPersonnelBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_PERSONNELID:
                    ++pos;
                    pObject.setPersonnelid(Manager.getInteger(rs, pos));
                    break;
                case ID_FNAME:
                    ++pos;
                    pObject.setFname(rs.getString(pos));
                    break;
                case ID_LNAME:
                    ++pos;
                    pObject.setLname(rs.getString(pos));
                    break;
                case ID_PHONE:
                    ++pos;
                    pObject.setPhone(rs.getString(pos));
                    break;
                case ID_PERSONNELTYPEID:
                    ++pos;
                    pObject.setPersonneltypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_LOGINID:
                    ++pos;
                    pObject.setLoginid(rs.getString(pos));
                    break;
                case ID_PASSWORD:
                    ++pos;
                    pObject.setPassword(rs.getString(pos));
                    break;
                case ID_EMAIL:
                    ++pos;
                    pObject.setEmail(rs.getString(pos));
                    break;
                case ID_ADDRESS:
                    ++pos;
                    pObject.setAddress(rs.getString(pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_DELETED:
                    ++pos;
                    pObject.setDeleted(Manager.getBoolean(rs, pos));
                    break;
                case ID_CHANGEBYID:
                    ++pos;
                    pObject.setChangebyid(Manager.getInteger(rs, pos));
                    break;
                case ID_CHANGEDATE:
                    ++pos;
                    pObject.setChangedate(rs.getTimestamp(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

