    /**
     * Transforms a ResultSet iterating on the preference on a PreferenceBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting PreferenceBean pObject
     */
    //72
    public PreferenceBean decodeRow(ResultSet rs) throws SQLException
    {
        PreferenceBean pObject = createPreferenceBean();
        pObject.setPreferenceid(Manager.getInteger(rs, 1));
        pObject.setName(rs.getString(2));
        pObject.setPreferencetypeid(Manager.getInteger(rs, 3));
        pObject.setRegbyid(Manager.getInteger(rs, 4));
        pObject.setRegdate(rs.getTimestamp(5));
        pObject.setActive(Manager.getBoolean(rs, 6));
        pObject.setDeleted(Manager.getBoolean(rs, 7));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the preference table on a PreferenceBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting PreferenceBean pObject
     */
    //73
    public PreferenceBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        PreferenceBean pObject = createPreferenceBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_PREFERENCEID:
                    ++pos;
                    pObject.setPreferenceid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_PREFERENCETYPEID:
                    ++pos;
                    pObject.setPreferencetypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_ACTIVE:
                    ++pos;
                    pObject.setActive(Manager.getBoolean(rs, pos));
                    break;
                case ID_DELETED:
                    ++pos;
                    pObject.setDeleted(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

