    /**
     * Transforms a ResultSet iterating on the report on a ReportBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting ReportBean pObject
     */
    //72
    public ReportBean decodeRow(ResultSet rs) throws SQLException
    {
        ReportBean pObject = createReportBean();
        pObject.setReportid(Manager.getInteger(rs, 1));
        pObject.setName(rs.getString(2));
        pObject.setDisplayname(rs.getString(3));
        pObject.setSqlquery(rs.getString(4));
        pObject.setRegbyid(Manager.getInteger(rs, 5));
        pObject.setRegdate(rs.getTimestamp(6));
        pObject.setRepcatid(Manager.getInteger(rs, 7));
        pObject.setOrd(Manager.getInteger(rs, 8));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the report table on a ReportBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting ReportBean pObject
     */
    //73
    public ReportBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        ReportBean pObject = createReportBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_REPORTID:
                    ++pos;
                    pObject.setReportid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_DISPLAYNAME:
                    ++pos;
                    pObject.setDisplayname(rs.getString(pos));
                    break;
                case ID_SQLQUERY:
                    ++pos;
                    pObject.setSqlquery(rs.getString(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_REPCATID:
                    ++pos;
                    pObject.setRepcatid(Manager.getInteger(rs, pos));
                    break;
                case ID_ORD:
                    ++pos;
                    pObject.setOrd(Manager.getInteger(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

