    /**
     * Transforms a ResultSet iterating on the reportitem on a ReportitemBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting ReportitemBean pObject
     */
    //72
    public ReportitemBean decodeRow(ResultSet rs) throws SQLException
    {
        ReportitemBean pObject = createReportitemBean();
        pObject.setReportitemid(Manager.getLong(rs, 1));
        pObject.setReportid(Manager.getInteger(rs, 2));
        pObject.setName(rs.getString(3));
        pObject.setIdx(Manager.getInteger(rs, 4));
        pObject.setFieldtype(Manager.getInteger(rs, 5));
        pObject.setAlign(rs.getString(6));
        pObject.setWd(Manager.getInteger(rs, 7));
        pObject.setHassum(Manager.getBoolean(rs, 8));
        pObject.setOrdered(Manager.getBoolean(rs, 9));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the reportitem table on a ReportitemBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting ReportitemBean pObject
     */
    //73
    public ReportitemBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        ReportitemBean pObject = createReportitemBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_REPORTITEMID:
                    ++pos;
                    pObject.setReportitemid(Manager.getLong(rs, pos));
                    break;
                case ID_REPORTID:
                    ++pos;
                    pObject.setReportid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_IDX:
                    ++pos;
                    pObject.setIdx(Manager.getInteger(rs, pos));
                    break;
                case ID_FIELDTYPE:
                    ++pos;
                    pObject.setFieldtype(Manager.getInteger(rs, pos));
                    break;
                case ID_ALIGN:
                    ++pos;
                    pObject.setAlign(rs.getString(pos));
                    break;
                case ID_WD:
                    ++pos;
                    pObject.setWd(Manager.getInteger(rs, pos));
                    break;
                case ID_HASSUM:
                    ++pos;
                    pObject.setHassum(Manager.getBoolean(rs, pos));
                    break;
                case ID_ORDERED:
                    ++pos;
                    pObject.setOrdered(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

