    /**
     * Transforms a ResultSet iterating on the reportsearch on a ReportsearchBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting ReportsearchBean pObject
     */
    //72
    public ReportsearchBean decodeRow(ResultSet rs) throws SQLException
    {
        ReportsearchBean pObject = createReportsearchBean();
        pObject.setReportsearchid(Manager.getInteger(rs, 1));
        pObject.setReportid(Manager.getInteger(rs, 2));
        pObject.setName(rs.getString(3));
        pObject.setType(Manager.getInteger(rs, 4));
        pObject.setSql(rs.getString(5));
        pObject.setMandatory(Manager.getBoolean(rs, 6));
        pObject.setDefval1(rs.getString(7));
        pObject.setDefval2(rs.getString(8));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the reportsearch table on a ReportsearchBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting ReportsearchBean pObject
     */
    //73
    public ReportsearchBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        ReportsearchBean pObject = createReportsearchBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_REPORTSEARCHID:
                    ++pos;
                    pObject.setReportsearchid(Manager.getInteger(rs, pos));
                    break;
                case ID_REPORTID:
                    ++pos;
                    pObject.setReportid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_TYPE:
                    ++pos;
                    pObject.setType(Manager.getInteger(rs, pos));
                    break;
                case ID_SQL:
                    ++pos;
                    pObject.setSql(rs.getString(pos));
                    break;
                case ID_MANDATORY:
                    ++pos;
                    pObject.setMandatory(Manager.getBoolean(rs, pos));
                    break;
                case ID_DEFVAL1:
                    ++pos;
                    pObject.setDefval1(rs.getString(pos));
                    break;
                case ID_DEFVAL2:
                    ++pos;
                    pObject.setDefval2(rs.getString(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

