    /**
     * Transforms a ResultSet iterating on the reservationreason on a ReservationreasonBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting ReservationreasonBean pObject
     */
    //72
    public ReservationreasonBean decodeRow(ResultSet rs) throws SQLException
    {
        ReservationreasonBean pObject = createReservationreasonBean();
        pObject.setReservationid(Manager.getLong(rs, 1));
        pObject.setReasonid(Manager.getInteger(rs, 2));
        pObject.setRegbyid(Manager.getInteger(rs, 3));
        pObject.setRegdate(rs.getTimestamp(4));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the reservationreason table on a ReservationreasonBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting ReservationreasonBean pObject
     */
    //73
    public ReservationreasonBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        ReservationreasonBean pObject = createReservationreasonBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_RESERVATIONID:
                    ++pos;
                    pObject.setReservationid(Manager.getLong(rs, pos));
                    break;
                case ID_REASONID:
                    ++pos;
                    pObject.setReasonid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

