    public RoomhstlogBean decodeRow(ResultSet rs) throws SQLException
    {
        RoomhstlogBean pObject = createRoomhstlogBean();
        pObject.setRoomhstlogid(Manager.getLong(rs, 1));
        pObject.setRoomid(Manager.getInteger(rs, 2));
        pObject.setHousekeepingstatusid(Manager.getInteger(rs, 3));
        pObject.setRegdate(rs.getTimestamp(4));
        pObject.setRegbyid(Manager.getInteger(rs, 5));
        pObject.setHouseunitid(Manager.getInteger(rs, 6));
        pObject.setClosedate(rs.getTimestamp(7));
        pObject.setPersonnelid(Manager.getInteger(rs, 8));
        pObject.setRemark(rs.getString(9));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the roomhstlog table on a RoomhstlogBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting RoomhstlogBean pObject
     */
    //73
    public RoomhstlogBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        RoomhstlogBean pObject = createRoomhstlogBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_ROOMHSTLOGID:
                    ++pos;
                    pObject.setRoomhstlogid(Manager.getLong(rs, pos));
                    break;
                case ID_ROOMID:
                    ++pos;
                    pObject.setRoomid(Manager.getInteger(rs, pos));
                    break;
                case ID_HOUSEKEEPINGSTATUSID:
                    ++pos;
                    pObject.setHousekeepingstatusid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_HOUSEUNITID:
                    ++pos;
                    pObject.setHouseunitid(Manager.getInteger(rs, pos));
                    break;
                case ID_CLOSEDATE:
                    ++pos;
                    pObject.setClosedate(rs.getTimestamp(pos));
                    break;
                case ID_PERSONNELID:
                    ++pos;
                    pObject.setPersonnelid(Manager.getInteger(rs, pos));
                    break;
                case ID_REMARK:
                    ++pos;
                    pObject.setRemark(rs.getString(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

