    /**
     * Transforms a ResultSet iterating on the template on a TemplateBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting TemplateBean pObject
     */
    //72
    public TemplateBean decodeRow(ResultSet rs) throws SQLException
    {
        TemplateBean pObject = createTemplateBean();
        pObject.setTemplateid(Manager.getInteger(rs, 1));
        pObject.setName(rs.getString(2));
        pObject.setTemplatecategoryid(Manager.getInteger(rs, 3));
        pObject.setSubject(rs.getString(4));
        pObject.setPersonnelid(Manager.getInteger(rs, 5));
        pObject.setCc(rs.getString(6));
        pObject.setBcc(rs.getString(7));
        pObject.setContent(rs.getString(8));
        pObject.setRegbyid(Manager.getInteger(rs, 9));
        pObject.setRegdate(rs.getTimestamp(10));
        pObject.setActive(Manager.getBoolean(rs, 11));
        pObject.setDeleted(Manager.getBoolean(rs, 12));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the template table on a TemplateBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting TemplateBean pObject
     */
    //73
    public TemplateBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        TemplateBean pObject = createTemplateBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_TEMPLATEID:
                    ++pos;
                    pObject.setTemplateid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_TEMPLATECATEGORYID:
                    ++pos;
                    pObject.setTemplatecategoryid(Manager.getInteger(rs, pos));
                    break;
                case ID_SUBJECT:
                    ++pos;
                    pObject.setSubject(rs.getString(pos));
                    break;
                case ID_PERSONNELID:
                    ++pos;
                    pObject.setPersonnelid(Manager.getInteger(rs, pos));
                    break;
                case ID_CC:
                    ++pos;
                    pObject.setCc(rs.getString(pos));
                    break;
                case ID_BCC:
                    ++pos;
                    pObject.setBcc(rs.getString(pos));
                    break;
                case ID_CONTENT:
                    ++pos;
                    pObject.setContent(rs.getString(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_ACTIVE:
                    ++pos;
                    pObject.setActive(Manager.getBoolean(rs, pos));
                    break;
                case ID_DELETED:
                    ++pos;
                    pObject.setDeleted(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

