    /**
     * Transforms a ResultSet iterating on the v_contragentlist table on a VContragentlistBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting VContragentlistBean pObject
     */
    //73
    public VContragentlistBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        VContragentlistBean pObject = createVContragentlistBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_CONTRAGENTID:
                    ++pos;
                    pObject.setContragentid(Manager.getLong(rs, pos));
                    break;
                case ID_CONTRAGENT:
                    ++pos;
                    pObject.setContragent(rs.getString(pos));
                    break;
                case ID_COMPANY:
                    ++pos;
                    pObject.setCompany(rs.getString(pos));
                    break;
                case ID_CITY:
                    ++pos;
                    pObject.setCity(rs.getString(pos));
                    break;
                case ID_TYPE:
                    ++pos;
                    pObject.setType(Manager.getInteger(rs, pos));
                    break;
                case ID_COUNTRYID:
                    ++pos;
                    pObject.setCountryid(Manager.getInteger(rs, pos));
                    break;
                case ID_COUNTRY:
                    ++pos;
                    pObject.setCountry(rs.getString(pos));
                    break;
                case ID_EMAIL:
                    ++pos;
                    pObject.setEmail(rs.getString(pos));
                    break;
                case ID_PHONE:
                    ++pos;
                    pObject.setPhone(rs.getString(pos));
                    break;
                case ID_MOBILE:
                    ++pos;
                    pObject.setMobile(rs.getString(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

