    //72
    public VGuestdblistBean decodeRow(ResultSet rs) throws SQLException
    {
        VGuestdblistBean pObject = createVGuestdblistBean();
        pObject.setGuest(rs.getString(1));
        pObject.setGuestid(Manager.getLong(rs, 2));
        pObject.setCity(rs.getString(3));
        pObject.setPhone(rs.getString(4));
        pObject.setMobile(rs.getString(5));
        pObject.setEmail(rs.getString(6));
        pObject.setCountry(rs.getString(7));
        pObject.setVipstatus(rs.getString(8));
        pObject.setSalutationid(Manager.getInteger(rs, 9));
        pObject.setCountryid(Manager.getInteger(rs, 10));
        pObject.setVipstatusid(Manager.getInteger(rs, 11));
        pObject.setDeleted(Manager.getBoolean(rs, 12));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the v_guestdblist table on a VGuestdblistBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting VGuestdblistBean pObject
     */
    //73
    public VGuestdblistBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        VGuestdblistBean pObject = createVGuestdblistBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_GUEST:
                    ++pos;
                    pObject.setGuest(rs.getString(pos));
                    break;
                case ID_GUESTID:
                    ++pos;
                    pObject.setGuestid(Manager.getLong(rs, pos));
                    break;
                case ID_CITY:
                    ++pos;
                    pObject.setCity(rs.getString(pos));
                    break;
                case ID_PHONE:
                    ++pos;
                    pObject.setPhone(rs.getString(pos));
                    break;
                case ID_MOBILE:
                    ++pos;
                    pObject.setMobile(rs.getString(pos));
                    break;
                case ID_EMAIL:
                    ++pos;
                    pObject.setEmail(rs.getString(pos));
                    break;
                case ID_COUNTRY:
                    ++pos;
                    pObject.setCountry(rs.getString(pos));
                    break;
                case ID_VIPSTATUS:
                    ++pos;
                    pObject.setVipstatus(rs.getString(pos));
                    break;
                case ID_SALUTATIONID:
                    ++pos;
                    pObject.setSalutationid(Manager.getInteger(rs, pos));
                    break;
                case ID_COUNTRYID:
                    ++pos;
                    pObject.setCountryid(Manager.getInteger(rs, pos));
                    break;
                case ID_VIPSTATUSID:
                    ++pos;
                    pObject.setVipstatusid(Manager.getInteger(rs, pos));
                    break;
                case ID_DELETED:
                    ++pos;
                    pObject.setDeleted(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

