    /**
     * Transforms a ResultSet iterating on the vipstatus on a VipstatusBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting VipstatusBean pObject
     */
    //72
    public VipstatusBean decodeRow(ResultSet rs) throws SQLException
    {
        VipstatusBean pObject = createVipstatusBean();
        pObject.setVipstatusid(Manager.getInteger(rs, 1));
        pObject.setName(Manager.getInteger(rs, 2));
        pObject.setActive(Manager.getBoolean(rs, 3));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the vipstatus table on a VipstatusBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting VipstatusBean pObject
     */
    //73
    public VipstatusBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        VipstatusBean pObject = createVipstatusBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_VIPSTATUSID:
                    ++pos;
                    pObject.setVipstatusid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(Manager.getInteger(rs, pos));
                    break;
                case ID_ACTIVE:
                    ++pos;
                    pObject.setActive(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

