    /**
     * Transforms a ResultSet iterating on the vs_activity table on a VsActivityBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting VsActivityBean pObject
     */
    //73
    public VsActivityBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        VsActivityBean pObject = createVsActivityBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_RESERVATIONID:
                    ++pos;
                    pObject.setReservationid(Manager.getLong(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_GUESTNAME:
                    ++pos;
                    pObject.setGuestname(rs.getString(pos));
                    break;
                case ID_ARRAIVALDATE:
                    ++pos;
                    pObject.setArraivaldate(rs.getTimestamp(pos));
                    break;
                case ID_DEPARTUREDATE:
                    ++pos;
                    pObject.setDeparturedate(rs.getTimestamp(pos));
                    break;
                case ID_NIGHTS:
                    ++pos;
                    pObject.setNights(Manager.getInteger(rs, pos));
                    break;
                case ID_ROOMNAME:
                    ++pos;
                    pObject.setRoomname(rs.getString(pos));
                    break;
                case ID_BSOURCE:
                    ++pos;
                    pObject.setBsource(rs.getString(pos));
                    break;
                case ID_TANAME:
                    ++pos;
                    pObject.setTaname(rs.getString(pos));
                    break;
                case ID_VOUCHER:
                    ++pos;
                    pObject.setVoucher(rs.getString(pos));
                    break;
                case ID_RATETYPE:
                    ++pos;
                    pObject.setRatetype(rs.getString(pos));
                    break;
                case ID_NRML:
                    ++pos;
                    pObject.setNrml(Manager.getDouble(rs, pos));
                    break;
                case ID_OFFERED:
                    ++pos;
                    pObject.setOffered(Manager.getDouble(rs, pos));
                    break;
                case ID_PAYMENTMETHOD:
                    ++pos;
                    pObject.setPaymentmethod(rs.getString(pos));
                    break;
                case ID_AUTHOR:
                    ++pos;
                    pObject.setAuthor(rs.getString(pos));
                    break;
                case ID_TAID:
                    ++pos;
                    pObject.setTaid(Manager.getLong(rs, pos));
                    break;
                case ID_RATETYPEID:
                    ++pos;
                    pObject.setRatetypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_PERSONNELID:
                    ++pos;
                    pObject.setPersonnelid(Manager.getInteger(rs, pos));
                    break;
                case ID_ROOMTYPEID:
                    ++pos;
                    pObject.setRoomtypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_BID:
                    ++pos;
                    pObject.setBid(Manager.getInteger(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    //////////////////////////////////////
    // PREPARED STATEMENT LOADER
    //////////////////////////////////////

    /**
     * Loads all the elements using a prepared statement.
     *
     * @param ps the PreparedStatement to be used
     * @return an array of VsActivityBean 
     */
    //41
    public VsActivityBean[] loadByPreparedStatement(PreparedStatement ps) throws SQLException
    {
        return loadByPreparedStatement(ps, null);
    }

    /**
     * Loads all the elements using a prepared statement specifying a list of fields to be retrieved.
     *
     * @param ps the PreparedStatement to be used
     * @param fieldList table of the field's associated constants
     * @return an array of VsActivityBean 
     */
    public VsActivityBean[] loadByPreparedStatement(PreparedStatement ps, int[] fieldList) throws SQLException
    {
        ResultSet rs =  null;
        java.util.ArrayList v =  null;
        try
        {
            rs =  ps.executeQuery();
            v = new java.util.ArrayList();
            while(rs.next())
            {
                if(fieldList == null)
                    v.add(decodeRow(rs));
                else 
                    v.add(decodeRow(rs, fieldList));
            }
            return (VsActivityBean[])v.toArray(new VsActivityBean[0]);
        }
        finally
        {
            if (v != null) { v.clear(); v = null;}
            getManager().close(rs);
        }
    }

