    /**
     * Transforms a ResultSet iterating on the vs_arrivallist on a VsArrivallistBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting VsArrivallistBean pObject
     */
    //72
    public VsArrivallistBean decodeRow(ResultSet rs) throws SQLException
    {
        VsArrivallistBean pObject = createVsArrivallistBean();
        pObject.setReservationid(Manager.getLong(rs, 1));
        pObject.setGuestname(rs.getString(2));
        pObject.setName(rs.getString(3));
        pObject.setFloat8(Manager.getDouble(rs, 4));
        pObject.setArraivaldate(rs.getTimestamp(5));
        pObject.setDeparturedate(rs.getTimestamp(6));
        pObject.setPax(rs.getString(7));
        pObject.setRtype(rs.getString(8));
        pObject.setCompany(rs.getString(9));
        pObject.setUser(rs.getString(10));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the vs_arrivallist table on a VsArrivallistBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting VsArrivallistBean pObject
     */
    //73
    public VsArrivallistBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        VsArrivallistBean pObject = createVsArrivallistBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_RESERVATIONID:
                    ++pos;
                    pObject.setReservationid(Manager.getLong(rs, pos));
                    break;
                case ID_GUESTNAME:
                    ++pos;
                    pObject.setGuestname(rs.getString(pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_FLOAT8:
                    ++pos;
                    pObject.setFloat8(Manager.getDouble(rs, pos));
                    break;
                case ID_ARRAIVALDATE:
                    ++pos;
                    pObject.setArraivaldate(rs.getTimestamp(pos));
                    break;
                case ID_DEPARTUREDATE:
                    ++pos;
                    pObject.setDeparturedate(rs.getTimestamp(pos));
                    break;
                case ID_PAX:
                    ++pos;
                    pObject.setPax(rs.getString(pos));
                    break;
                case ID_RTYPE:
                    ++pos;
                    pObject.setRtype(rs.getString(pos));
                    break;
                case ID_COMPANY:
                    ++pos;
                    pObject.setCompany(rs.getString(pos));
                    break;
                case ID_USER:
                    ++pos;
                    pObject.setUser(rs.getString(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

