    /**
     * Transforms a ResultSet iterating on the vs_cityledger on a VsCityledgerBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting VsCityledgerBean pObject
     */
    //72
    public VsCityledgerBean decodeRow(ResultSet rs) throws SQLException
    {
        VsCityledgerBean pObject = createVsCityledgerBean();
        pObject.setRegdate(rs.getTimestamp(1));
        pObject.setGuest(rs.getString(2));
        pObject.setArraivaldate(rs.getTimestamp(3));
        pObject.setDeparturedate(rs.getTimestamp(4));
        pObject.setReference(rs.getString(5));
        pObject.setCredits(rs.getObject(6));
        pObject.setDebits(rs.getObject(7));
        pObject.setBalance(rs.getObject(8));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the vs_cityledger table on a VsCityledgerBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting VsCityledgerBean pObject
     */
    //73
    public VsCityledgerBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        VsCityledgerBean pObject = createVsCityledgerBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_GUEST:
                    ++pos;
                    pObject.setGuest(rs.getString(pos));
                    break;
                case ID_ARRAIVALDATE:
                    ++pos;
                    pObject.setArraivaldate(rs.getTimestamp(pos));
                    break;
                case ID_DEPARTUREDATE:
                    ++pos;
                    pObject.setDeparturedate(rs.getTimestamp(pos));
                    break;
                case ID_REFERENCE:
                    ++pos;
                    pObject.setReference(rs.getString(pos));
                    break;
                case ID_CREDITS:
                    ++pos;
                    pObject.setCredits(rs.getObject(pos));
                    break;
                case ID_DEBITS:
                    ++pos;
                    pObject.setDebits(rs.getObject(pos));
                    break;
                case ID_BALANCE:
                    ++pos;
                    pObject.setBalance(rs.getObject(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

