    /**
     * Transforms a ResultSet iterating on the workorder on a WorkorderBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting WorkorderBean pObject
     */
    //72
    public WorkorderBean decodeRow(ResultSet rs) throws SQLException
    {
        WorkorderBean pObject = createWorkorderBean();
        pObject.setWorkorderid(Manager.getLong(rs, 1));
        pObject.setNum(rs.getString(2));
        pObject.setDescription(rs.getString(3));
        pObject.setCategory(Manager.getInteger(rs, 4));
        pObject.setRoomid(Manager.getInteger(rs, 5));
        pObject.setHouseunitid(Manager.getInteger(rs, 6));
        pObject.setBlockstart(rs.getTimestamp(7));
        pObject.setBlockend(rs.getTimestamp(8));
        pObject.setPriority(Manager.getInteger(rs, 9));
        pObject.setAssignedtoid(Manager.getInteger(rs, 10));
        pObject.setOrderstatus(Manager.getInteger(rs, 11));
        pObject.setDeadline(rs.getTimestamp(12));
        pObject.setRegdate(rs.getTimestamp(13));
        pObject.setRegbyid(Manager.getInteger(rs, 14));
        pObject.setUpdatedon(rs.getTimestamp(15));
        pObject.setNote(rs.getString(16));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the workorder table on a WorkorderBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting WorkorderBean pObject
     */
    //73
    public WorkorderBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        WorkorderBean pObject = createWorkorderBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_WORKORDERID:
                    ++pos;
                    pObject.setWorkorderid(Manager.getLong(rs, pos));
                    break;
                case ID_NUM:
                    ++pos;
                    pObject.setNum(rs.getString(pos));
                    break;
                case ID_DESCRIPTION:
                    ++pos;
                    pObject.setDescription(rs.getString(pos));
                    break;
                case ID_CATEGORY:
                    ++pos;
                    pObject.setCategory(Manager.getInteger(rs, pos));
                    break;
                case ID_ROOMID:
                    ++pos;
                    pObject.setRoomid(Manager.getInteger(rs, pos));
                    break;
                case ID_HOUSEUNITID:
                    ++pos;
                    pObject.setHouseunitid(Manager.getInteger(rs, pos));
                    break;
                case ID_BLOCKSTART:
                    ++pos;
                    pObject.setBlockstart(rs.getTimestamp(pos));
                    break;
                case ID_BLOCKEND:
                    ++pos;
                    pObject.setBlockend(rs.getTimestamp(pos));
                    break;
                case ID_PRIORITY:
                    ++pos;
                    pObject.setPriority(Manager.getInteger(rs, pos));
                    break;
                case ID_ASSIGNEDTOID:
                    ++pos;
                    pObject.setAssignedtoid(Manager.getInteger(rs, pos));
                    break;
                case ID_ORDERSTATUS:
                    ++pos;
                    pObject.setOrderstatus(Manager.getInteger(rs, pos));
                    break;
                case ID_DEADLINE:
                    ++pos;
                    pObject.setDeadline(rs.getTimestamp(pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_UPDATEDON:
                    ++pos;
                    pObject.setUpdatedon(rs.getTimestamp(pos));
                    break;
                case ID_NOTE:
                    ++pos;
                    pObject.setNote(rs.getString(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    //////////////////////////////////////
    // PREPARED STATEMENT LOADER
    //////////////////////////////////////

    /**
     * Loads all the elements using a prepared statement.
     *
     * @param ps the PreparedStatement to be used
     * @return an array of WorkorderBean 
     */
    //41
    public WorkorderBean[] loadByPreparedStatement(PreparedStatement ps) throws SQLException
    {
        return loadByPreparedStatement(ps, null);
    }

    /**
     * Loads all the elements using a prepared statement specifying a list of fields to be retrieved.
     *
     * @param ps the PreparedStatement to be used
     * @param fieldList table of the field's associated constants
     * @return an array of WorkorderBean 
     */
    public WorkorderBean[] loadByPreparedStatement(PreparedStatement ps, int[] fieldList) throws SQLException
    {
        ResultSet rs =  null;
        java.util.ArrayList v =  null;
        try
        {
            rs =  ps.executeQuery();
            v = new java.util.ArrayList();
            while(rs.next())
            {
                if(fieldList == null)
                    v.add(decodeRow(rs));
                else 
                    v.add(decodeRow(rs, fieldList));
            }
            return (WorkorderBean[])v.toArray(new WorkorderBean[0]);
        }
        finally
        {
            if (v != null) { v.clear(); v = null;}
            getManager().close(rs);
        }
    }

