    ///////////////////////////////////////////////////////////////////////
    // DECODE RESULT SET 
    ///////////////////////////////////////////////////////////////////////
    /**
     * Transforms a ResultSet iterating on the currency on a CurrencyBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting CurrencyBean pObject
     */
    //72
    public CurrencyBean decodeRow(ResultSet rs) throws SQLException
    {
        CurrencyBean pObject = createCurrencyBean();
        pObject.setCurrencyid(Manager.getInteger(rs, 1));
        pObject.setName(rs.getString(2));
        pObject.setCode(rs.getString(3));
        pObject.setBasic(Manager.getBoolean(rs, 4));
        pObject.setIcon(rs.getString(5));
        pObject.setCountryid(Manager.getInteger(rs, 6));
        pObject.setFixedexrate(Manager.getBoolean(rs, 7));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the currency table on a CurrencyBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting CurrencyBean pObject
     */
    //73
    public CurrencyBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        CurrencyBean pObject = createCurrencyBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_CURRENCYID:
                    ++pos;
                    pObject.setCurrencyid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_CODE:
                    ++pos;
                    pObject.setCode(rs.getString(pos));
                    break;
                case ID_BASIC:
                    ++pos;
                    pObject.setBasic(Manager.getBoolean(rs, pos));
                    break;
                case ID_ICON:
                    ++pos;
                    pObject.setIcon(rs.getString(pos));
                    break;
                case ID_COUNTRYID:
                    ++pos;
                    pObject.setCountryid(Manager.getInteger(rs, pos));
                    break;
                case ID_FIXEDEXRATE:
                    ++pos;
                    pObject.setFixedexrate(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

