    /**
     * Transforms a ResultSet iterating on the discount on a DiscountBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting DiscountBean pObject
     */
    //72
    public DiscountBean decodeRow(ResultSet rs) throws SQLException
    {
        DiscountBean pObject = createDiscountBean();
        pObject.setDiscountid(Manager.getInteger(rs, 1));
        pObject.setCode(rs.getString(2));
        pObject.setName(rs.getString(3));
        pObject.setPerctype(Manager.getBoolean(rs, 4));
        pObject.setOpen(Manager.getBoolean(rs, 5));
        pObject.setVal(Manager.getDouble(rs, 6));
        pObject.setRoomrate(Manager.getBoolean(rs, 7));
        pObject.setExtracharge(Manager.getBoolean(rs, 8));
        pObject.setRegbyid(Manager.getInteger(rs, 9));
        pObject.setRegdate(rs.getTimestamp(10));
        pObject.setActive(Manager.getBoolean(rs, 11));
        pObject.setDeleted(Manager.getBoolean(rs, 12));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the discount table on a DiscountBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting DiscountBean pObject
     */
    //73
    public DiscountBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        DiscountBean pObject = createDiscountBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_DISCOUNTID:
                    ++pos;
                    pObject.setDiscountid(Manager.getInteger(rs, pos));
                    break;
                case ID_CODE:
                    ++pos;
                    pObject.setCode(rs.getString(pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_PERCTYPE:
                    ++pos;
                    pObject.setPerctype(Manager.getBoolean(rs, pos));
                    break;
                case ID_OPEN:
                    ++pos;
                    pObject.setOpen(Manager.getBoolean(rs, pos));
                    break;
                case ID_VAL:
                    ++pos;
                    pObject.setVal(Manager.getDouble(rs, pos));
                    break;
                case ID_ROOMRATE:
                    ++pos;
                    pObject.setRoomrate(Manager.getBoolean(rs, pos));
                    break;
                case ID_EXTRACHARGE:
                    ++pos;
                    pObject.setExtracharge(Manager.getBoolean(rs, pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_ACTIVE:
                    ++pos;
                    pObject.setActive(Manager.getBoolean(rs, pos));
                    break;
                case ID_DELETED:
                    ++pos;
                    pObject.setDeleted(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    //////////////////////////////////////
    // PREPARED STATEMENT LOADER
    //////////////////////////////////////

    /**
     * Loads all the elements using a prepared statement.
     *
     * @param ps the PreparedStatement to be used
     * @return an array of DiscountBean 
     */
    //41
    public DiscountBean[] loadByPreparedStatement(PreparedStatement ps) throws SQLException
    {
        return loadByPreparedStatement(ps, null);
    }

    /**
     * Loads all the elements using a prepared statement specifying a list of fields to be retrieved.
     *
     * @param ps the PreparedStatement to be used
     * @param fieldList table of the field's associated constants
     * @return an array of DiscountBean 
     */
    public DiscountBean[] loadByPreparedStatement(PreparedStatement ps, int[] fieldList) throws SQLException
    {
        ResultSet rs =  null;
        java.util.ArrayList v =  null;
        try
        {
            rs =  ps.executeQuery();
            v = new java.util.ArrayList();
            while(rs.next())
            {
                if(fieldList == null)
                    v.add(decodeRow(rs));
                else 
                    v.add(decodeRow(rs, fieldList));
            }
            return (DiscountBean[])v.toArray(new DiscountBean[0]);
        }
        finally
        {
            if (v != null) { v.clear(); v = null;}
            getManager().close(rs);
        }
    }

