    /**
     * Transforms a ResultSet iterating on the extrachecktype table on a ExtrachecktypeBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting ExtrachecktypeBean pObject
     */
    //73
    public ExtrachecktypeBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        ExtrachecktypeBean pObject = createExtrachecktypeBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_EXTRACHECKTYPEID:
                    ++pos;
                    pObject.setExtrachecktypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_AUTOINCREMENT:
                    ++pos;
                    pObject.setAutoincrement(Manager.getBoolean(rs, pos));
                    break;
                case ID_PREFIX:
                    ++pos;
                    pObject.setPrefix(rs.getString(pos));
                    break;
                case ID_STARTVALUE:
                    ++pos;
                    pObject.setStartvalue(Manager.getLong(rs, pos));
                    break;
                case ID_RESETTYPE:
                    ++pos;
                    pObject.setResettype(Manager.getInteger(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

