    ///////////////////////////////////////////////////////////////////////
    /**
     * Transforms a ResultSet iterating on the personneltype on a PersonneltypeBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting PersonneltypeBean pObject
     */
    //72
    public PersonneltypeBean decodeRow(ResultSet rs) throws SQLException
    {
        PersonneltypeBean pObject = createPersonneltypeBean();
        pObject.setPersonneltypeid(Manager.getInteger(rs, 1));
        pObject.setName(rs.getString(2));
        pObject.setDescription(rs.getString(3));
        pObject.setCode(rs.getString(4));
        pObject.setDeleted(Manager.getBoolean(rs, 5));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the personneltype table on a PersonneltypeBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting PersonneltypeBean pObject
     */
    //73
    public PersonneltypeBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        PersonneltypeBean pObject = createPersonneltypeBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_PERSONNELTYPEID:
                    ++pos;
                    pObject.setPersonneltypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_DESCRIPTION:
                    ++pos;
                    pObject.setDescription(rs.getString(pos));
                    break;
                case ID_CODE:
                    ++pos;
                    pObject.setCode(rs.getString(pos));
                    break;
                case ID_DELETED:
                    ++pos;
                    pObject.setDeleted(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

