    /**
     * Looks for the number of elements of a specific PlevelBean pObject given a c
     *
     * @param pObject the PlevelBean pObject to look for
     * @return the number of rows returned
     */
    //83
    public int countUsingTemplate(PlevelBean pObject) throws SQLException
    {
        StringBuffer where = new StringBuffer("");
        Connection c = null;
        PreparedStatement ps = null;
        StringBuffer _sql = null;
        StringBuffer _sqlWhere = null;
    
        try
        {
                _sql = new StringBuffer("SELECT count(*) as MCOUNT  from plevel WHERE ");
                _sqlWhere = new StringBuffer("");
                int _dirtyCount = 0;
    
                if (pObject.isPlevelidModified()) {
                    _dirtyCount++; 
                    _sqlWhere.append((_sqlWhere.length() == 0) ? " " : " AND ").append("plevelid= ?");
                }
    
                if (pObject.isPersonneltypeidModified()) {
                    _dirtyCount++; 
                    _sqlWhere.append((_sqlWhere.length() == 0) ? " " : " AND ").append("personneltypeid= ?");
                }
    
                if (pObject.isMenuidModified()) {
                    _dirtyCount++; 
                    _sqlWhere.append((_sqlWhere.length() == 0) ? " " : " AND ").append("menuid= ?");
                }
    
                if (pObject.isReadModified()) {
                    _dirtyCount++; 
                    _sqlWhere.append((_sqlWhere.length() == 0) ? " " : " AND ").append("read= ?");
                }
    
                if (pObject.isWriteModified()) {
                    _dirtyCount++; 
                    _sqlWhere.append((_sqlWhere.length() == 0) ? " " : " AND ").append("write= ?");
                }
    
                if (_dirtyCount == 0)
                   throw new SQLException ("The pObject to look is unvalid : not initialized !");
    
                _sql.append(_sqlWhere);
                c = getConnection();
                ps = c.prepareStatement(_sql.toString(),ResultSet.TYPE_SCROLL_INSENSITIVE, ResultSet.CONCUR_READ_ONLY);
    
                _dirtyCount = 0;
    
                if (pObject.isPlevelidModified()) {
                    Manager.setInteger(ps, ++_dirtyCount, pObject.getPlevelid());
                }
    
                if (pObject.isPersonneltypeidModified()) {
                    Manager.setInteger(ps, ++_dirtyCount, pObject.getPersonneltypeid());
                }
    
                if (pObject.isMenuidModified()) {
                    Manager.setInteger(ps, ++_dirtyCount, pObject.getMenuid());
                }
    
                if (pObject.isReadModified()) {
                    Manager.setBoolean(ps, ++_dirtyCount, pObject.getRead());
                }
    
                if (pObject.isWriteModified()) {
                    Manager.setBoolean(ps, ++_dirtyCount, pObject.getWrite());
                }
    
                return countByPreparedStatement(ps);
        }
        finally
        {
            getManager().close(ps);
            freeConnection(c);
        }
    }



