    /**
     * Transforms a ResultSet iterating on the plevel on a PlevelBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting PlevelBean pObject
     */
    //72
    public PlevelBean decodeRow(ResultSet rs) throws SQLException
    {
        PlevelBean pObject = createPlevelBean();
        pObject.setPlevelid(Manager.getInteger(rs, 1));
        pObject.setPersonneltypeid(Manager.getInteger(rs, 2));
        pObject.setMenuid(Manager.getInteger(rs, 3));
        pObject.setRead(Manager.getBoolean(rs, 4));
        pObject.setWrite(Manager.getBoolean(rs, 5));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the plevel table on a PlevelBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting PlevelBean pObject
     */
    //73
    public PlevelBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        PlevelBean pObject = createPlevelBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_PLEVELID:
                    ++pos;
                    pObject.setPlevelid(Manager.getInteger(rs, pos));
                    break;
                case ID_PERSONNELTYPEID:
                    ++pos;
                    pObject.setPersonneltypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_MENUID:
                    ++pos;
                    pObject.setMenuid(Manager.getInteger(rs, pos));
                    break;
                case ID_READ:
                    ++pos;
                    pObject.setRead(Manager.getBoolean(rs, pos));
                    break;
                case ID_WRITE:
                    ++pos;
                    pObject.setWrite(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

