    // DECODE RESULT SET 
    ///////////////////////////////////////////////////////////////////////
    /**
     * Transforms a ResultSet iterating on the product on a ProductBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting ProductBean pObject
     */
    //72
    public ProductBean decodeRow(ResultSet rs) throws SQLException
    {
        ProductBean pObject = createProductBean();
        pObject.setProductid(Manager.getInteger(rs, 1));
        pObject.setName(rs.getString(2));
        pObject.setDescription(rs.getString(3));
        pObject.setProducttypeid(Manager.getInteger(rs, 4));
        pObject.setUnitsid(Manager.getInteger(rs, 5));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the product table on a ProductBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting ProductBean pObject
     */
    //73
    public ProductBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        ProductBean pObject = createProductBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_PRODUCTID:
                    ++pos;
                    pObject.setProductid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_DESCRIPTION:
                    ++pos;
                    pObject.setDescription(rs.getString(pos));
                    break;
                case ID_PRODUCTTYPEID:
                    ++pos;
                    pObject.setProducttypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_UNITSID:
                    ++pos;
                    pObject.setUnitsid(Manager.getInteger(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

