    public ProductpropertyBean decodeRow(ResultSet rs) throws SQLException
    {
        ProductpropertyBean pObject = createProductpropertyBean();
        pObject.setProductpropertyid(Manager.getLong(rs, 1));
        pObject.setProducttypeid(Manager.getInteger(rs, 2));
        pObject.setName(rs.getString(3));
        pObject.setType(rs.getString(4));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the productproperty table on a ProductpropertyBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting ProductpropertyBean pObject
     */
    //73
    public ProductpropertyBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        ProductpropertyBean pObject = createProductpropertyBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_PRODUCTPROPERTYID:
                    ++pos;
                    pObject.setProductpropertyid(Manager.getLong(rs, pos));
                    break;
                case ID_PRODUCTTYPEID:
                    ++pos;
                    pObject.setProducttypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_TYPE:
                    ++pos;
                    pObject.setType(rs.getString(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

