    /**
     * Transforms a ResultSet iterating on the reservationroom on a ReservationroomBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting ReservationroomBean pObject
     */
    //72
    public ReservationroomBean decodeRow(ResultSet rs) throws SQLException
    {
        ReservationroomBean pObject = createReservationroomBean();
        pObject.setReservationroomid(Manager.getLong(rs, 1));
        pObject.setReservationid(Manager.getLong(rs, 2));
        pObject.setRoomid(Manager.getInteger(rs, 3));
        pObject.setLeader(Manager.getBoolean(rs, 4));
        pObject.setAdult(Manager.getInteger(rs, 5));
        pObject.setChild(Manager.getInteger(rs, 6));
        pObject.setRatetypeid(Manager.getInteger(rs, 7));
        pObject.setGuestid(Manager.getLong(rs, 8));
        pObject.setRegbyid(Manager.getInteger(rs, 9));
        pObject.setRegdate(rs.getTimestamp(10));
        pObject.setRoomtypeid(Manager.getInteger(rs, 11));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the reservationroom table on a ReservationroomBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting ReservationroomBean pObject
     */
    //73
    public ReservationroomBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        ReservationroomBean pObject = createReservationroomBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_RESERVATIONROOMID:
                    ++pos;
                    pObject.setReservationroomid(Manager.getLong(rs, pos));
                    break;
                case ID_RESERVATIONID:
                    ++pos;
                    pObject.setReservationid(Manager.getLong(rs, pos));
                    break;
                case ID_ROOMID:
                    ++pos;
                    pObject.setRoomid(Manager.getInteger(rs, pos));
                    break;
                case ID_LEADER:
                    ++pos;
                    pObject.setLeader(Manager.getBoolean(rs, pos));
                    break;
                case ID_ADULT:
                    ++pos;
                    pObject.setAdult(Manager.getInteger(rs, pos));
                    break;
                case ID_CHILD:
                    ++pos;
                    pObject.setChild(Manager.getInteger(rs, pos));
                    break;
                case ID_RATETYPEID:
                    ++pos;
                    pObject.setRatetypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_GUESTID:
                    ++pos;
                    pObject.setGuestid(Manager.getLong(rs, pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_ROOMTYPEID:
                    ++pos;
                    pObject.setRoomtypeid(Manager.getInteger(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

