    /**
     * Transforms a ResultSet iterating on the reservationtype on a ReservationtypeBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting ReservationtypeBean pObject
     */
    //72
    public ReservationtypeBean decodeRow(ResultSet rs) throws SQLException
    {
        ReservationtypeBean pObject = createReservationtypeBean();
        pObject.setReservationtypeid(Manager.getInteger(rs, 1));
        pObject.setName(rs.getString(2));
        pObject.setConfirmed(Manager.getBoolean(rs, 3));
        pObject.setColor(rs.getString(4));
        pObject.setRegbyid(Manager.getInteger(rs, 5));
        pObject.setRegdate(rs.getTimestamp(6));
        pObject.setActive(Manager.getBoolean(rs, 7));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the reservationtype table on a ReservationtypeBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting ReservationtypeBean pObject
     */
    //73
    public ReservationtypeBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        ReservationtypeBean pObject = createReservationtypeBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_RESERVATIONTYPEID:
                    ++pos;
                    pObject.setReservationtypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_CONFIRMED:
                    ++pos;
                    pObject.setConfirmed(Manager.getBoolean(rs, pos));
                    break;
                case ID_COLOR:
                    ++pos;
                    pObject.setColor(rs.getString(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_ACTIVE:
                    ++pos;
                    pObject.setActive(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

