    /**
     * Transforms a ResultSet iterating on the v_arrivallist table on a VArrivallistBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting VArrivallistBean pObject
     */
    //73
    public VArrivallistBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        VArrivallistBean pObject = createVArrivallistBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_RESERVATIONID:
                    ++pos;
                    pObject.setReservationid(Manager.getLong(rs, pos));
                    break;
                case ID_RESERVATIONROOMID:
                    ++pos;
                    pObject.setReservationroomid(Manager.getLong(rs, pos));
                    break;
                case ID_ROOMID:
                    ++pos;
                    pObject.setRoomid(Manager.getInteger(rs, pos));
                    break;
                case ID_ROOMTYPEID:
                    ++pos;
                    pObject.setRoomtypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_ARRAIVALDATE:
                    ++pos;
                    pObject.setArraivaldate(rs.getTimestamp(pos));
                    break;
                case ID_DEPARTUREDATE:
                    ++pos;
                    pObject.setDeparturedate(rs.getTimestamp(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_RESERVATIONTYPEID:
                    ++pos;
                    pObject.setReservationtypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_GUESTID:
                    ++pos;
                    pObject.setGuestid(Manager.getLong(rs, pos));
                    break;
                case ID_GUEST:
                    ++pos;
                    pObject.setGuest(rs.getString(pos));
                    break;
                case ID_ROOMCODE:
                    ++pos;
                    pObject.setRoomcode(rs.getString(pos));
                    break;
                case ID_ROOMTYPECODE:
                    ++pos;
                    pObject.setRoomtypecode(rs.getString(pos));
                    break;
                case ID_BSOURCENAME:
                    ++pos;
                    pObject.setBsourcename(rs.getString(pos));
                    break;
                case ID_BSOURCEID:
                    ++pos;
                    pObject.setBsourceid(Manager.getInteger(rs, pos));
                    break;
                case ID_COMPANYNAME:
                    ++pos;
                    pObject.setCompanyname(rs.getString(pos));
                    break;
                case ID_ADULT:
                    ++pos;
                    pObject.setAdult(Manager.getInteger(rs, pos));
                    break;
                case ID_CHILD:
                    ++pos;
                    pObject.setChild(Manager.getInteger(rs, pos));
                    break;
                case ID_RESERVTYPE:
                    ++pos;
                    pObject.setReservtype(rs.getString(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

