    // DECODE RESULT SET 
    ///////////////////////////////////////////////////////////////////////
    /**
     * Transforms a ResultSet iterating on the v_tariff on a VTariffBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting VTariffBean pObject
     */
    //72
    public VTariffBean decodeRow(ResultSet rs) throws SQLException
    {
        VTariffBean pObject = createVTariffBean();
        pObject.setTid(Manager.getInteger(rs, 1));
        pObject.setRid(Manager.getInteger(rs, 2));
        pObject.setSid(Manager.getInteger(rs, 3));
        pObject.setAgentid(Manager.getLong(rs, 4));
        pObject.setAmount(Manager.getDouble(rs, 5));
        pObject.setAmountadult(Manager.getDouble(rs, 6));
        pObject.setAmountchild(Manager.getDouble(rs, 7));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the v_tariff table on a VTariffBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting VTariffBean pObject
     */
    //73
    public VTariffBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        VTariffBean pObject = createVTariffBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_TID:
                    ++pos;
                    pObject.setTid(Manager.getInteger(rs, pos));
                    break;
                case ID_RID:
                    ++pos;
                    pObject.setRid(Manager.getInteger(rs, pos));
                    break;
                case ID_SID:
                    ++pos;
                    pObject.setSid(Manager.getInteger(rs, pos));
                    break;
                case ID_AGENTID:
                    ++pos;
                    pObject.setAgentid(Manager.getLong(rs, pos));
                    break;
                case ID_AMOUNT:
                    ++pos;
                    pObject.setAmount(Manager.getDouble(rs, pos));
                    break;
                case ID_AMOUNTADULT:
                    ++pos;
                    pObject.setAmountadult(Manager.getDouble(rs, pos));
                    break;
                case ID_AMOUNTCHILD:
                    ++pos;
                    pObject.setAmountchild(Manager.getDouble(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

